import React from "react";
import axios from "axios";

class App extends React.Component {

  getProductInfo = async () => {
    let productsData = await axios.get('http://localhost:3001/products');

    this.setState({
      productsData: productsData.data
    })

    console.log(productsData.data);
  }

  componentDidMount(){
    this.getProductInfo();
  }
  render(){
    let productsToRender = this.state.productsData.map((prod, idx) => <p key={idx}>{prod.name}-{prod.price}-{prod.brand}-{prod.origin}-{prod.qty}</p> );
    return (
      <>
        <h1>Quick Store</h1>
        <h2>Built with MongoDB and AWS</h2>
        <h3>Snowboards</h3>

        {
          this.state.productsData.length > 0 &&
            {productsToRender}
        }
      </>
    );
  }
}
export default App;
