import React from "react";
import axios from "axios";
import { Accordion} from 'react-bootstrap';

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      productsData: []
    }
  }

  getProductInfo = async () => {
    let productsData = await axios.get('http://localhost:3001/products');

    this.setState({
      productsData: productsData.data
    })

    console.log(productsData.data);
  }

  componentDidMount() {
    this.getProductInfo();
  }
  render() {
    let productsToRender = this.state.productsData.map((prod, idx) =>
      <Accordion>
        <Accordion.Item eventKey="0">
          <Accordion.Header>Accordion Item #1</Accordion.Header>
          <Accordion.Body>
            {prod.name}-${prod.price}-{prod.brand}-{prod.origin}-{prod.qty}
          </Accordion.Body>
        </Accordion.Item>
      </Accordion>);
    return (
      <>
        <h1>Quick Store</h1>
        <h2>Built with MongoDB and AWS</h2>
        <h3>Snowboards</h3>

        {
          this.state.productsData.length > 0 &&
          <>
            {productsToRender}
          </>
        }
      </>
    );
  }
}
export default App;
